        new Rational(3, 4) | 2                   || 0.5625
        new Rational(1, 2) | -2                  || 4.0
        4                  | new Rational(1, 2)  || 2.0
        2                  | new Rational(1, 2)  || Math.sqrt(2)
        5                  | new Rational(1, 4)  || Math.pow(5, 0.25)

