        new Rational(1, 2) | new Rational(5, 2)  || 0.1767766952966369
        new Rational(3, 2) | new Rational(2, 1)  || 2.25
        new Rational(4, 9) | new Rational(-7, 8) || 2.033104508122151
        new Rational(8, 5) | new Rational(5, 4)  || 1.7994922406091172
    }

